import React, { useState } from 'react';
import './Contact.css';
import { FaEnvelope, FaPhone, FaFacebookF, FaTwitter, FaInstagram, FaLinkedinIn } from 'react-icons/fa';

const Contact = () => {
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Perform form submission logic (e.g., send data to a server)
    setIsSubmitted(true);
  };

  return (
    <section id="contact" className="contact">
      {isSubmitted ? (
        <div className="thank-you-message">
          <h2>Thank You!</h2>
          <p>Your message has been successfully sent. We will contact you soon.</p>
        </div>
      ) : (
        <div className="contact-container">
          <div className="contact-info">
            <h2>Contact Me</h2>
            <div className="info-item">
              <FaEnvelope className="info-icon" />
              <p>shashanks874pm@gmail.com</p>
            </div>
            <div className="info-item">
              <FaPhone className="info-icon" />
              <p>4255470422</p>
            </div>
            
            <div className="info-item">
              {/* <FaFacebookF className="social-icon" /> */}
              {/* <FaTwitter className="social-icon" /> */}
              <FaInstagram className="social-icon" />
              <FaLinkedinIn className="social-icon" />
            </div>
            <div className="resume-download">
          <a href="/resume.pdf" download="Shashank_Sankaregoda_Resume.pdf">
            <button className="download-button">Download Resume</button>
          </a>
        </div>
            {/* <a href="/path/to/cv.pdf" className="download-btn" download>Download CV</a> */}
          </div>
          <div className="contact-form">
            <form onSubmit={handleSubmit}>
              <input type="text" placeholder="Name" required />
              <input type="email" placeholder="Email" required />
              <textarea placeholder="Message" required></textarea>
              <button type="submit" className="submit-btn">Submit</button>
            </form>
          </div>
        </div>
      )}
    </section>
  );
};

export default Contact;
