import React, { useState, useEffect } from 'react';
import './Home.css';
import profileImage from '../assets/picmine.jpg'; // Ensure this path is correct

const Home = () => {
  const fullName = "Hi, I'm Shashank Shankaregowda";
  const [displayedName, setDisplayedName] = useState(''); // State to store the current text

  useEffect(() => {
    let index = 0;
    const interval = setInterval(() => {
      setDisplayedName((prev) => prev + fullName.charAt(index));
      index++;
      if (index >= fullName.length) {
        clearInterval(interval); // Clear interval when the text is fully displayed
      }
    }, 10); // Adjust the speed (100ms per character)

    return () => clearInterval(interval); // Cleanup interval on unmount
  }, []);

  return (
    <section id="home" className="home">
      <div className="home-content">
        <div className="home-text">
          <p className="subtitle"></p>
          <h1 className="typewriter">{displayedName}</h1> {/* Typewriter effect here */}
          <p className="location">Innovating Software Engineering with Scalable Solutions and AI</p>
        </div>
        <img src={profileImage} alt="Shashank Shankaregowda" className="profile-image"/>
      </div>
    </section>
  );
};

export default Home;
