import React, { useState } from 'react';
import './Experience.css';
import DataLogo from "../assets/DataLogo.jpeg";
import BrightLogo from "../assets/BrightLogo.jpeg";

const Services = () => {
  const [isDatalyzerExpanded, setIsDatalyzerExpanded] = useState(false); // State for Datalyzer
  const [isBrightChampsExpanded, setIsBrightChampsExpanded] = useState(false); // State for BrightChamps

  const toggleDatalyzerDetails = () => {
    setIsDatalyzerExpanded(!isDatalyzerExpanded); // Toggle detailed information for Datalyzer
  };

  const toggleBrightChampsDetails = () => {
    setIsBrightChampsExpanded(!isBrightChampsExpanded); // Toggle detailed information for BrightChamps
  };

  return (
    <section id="services" className="services">
      <h2>Experience</h2>
      <div className="services-container">
        {/* Datalyzer Section */}
        <div className="service-item">
          <img src={DataLogo} className="service-icon" alt="Datalyzer Logo" />
          <h3>Datalyzer</h3>
          <p>Software Engineer</p>
          <p id='des'>
            Two years of professional experience in both software engineering and education. 
            As a Software Engineer at Datalyzer International Inc. (May 2022 – July 2023)
          </p>
          {isDatalyzerExpanded && (
            <div className="detailed-description">
              <p>
                During my time at Datalyzer, I spearheaded the design and implementation of scalable RESTful APIs, 
                optimized relational database schemas, and contributed to front-end development with Angular.
              </p>
              <p>
                My work reduced downtime by 30% and improved system performance, resulting in a 50% faster page load time.
              </p>
            </div>
          )}
          <a href="#learn-more" onClick={toggleDatalyzerDetails}>
            {isDatalyzerExpanded ? 'Show less' : 'Learn more'}
          </a>
        </div>

        {/* BrightChamps Section */}
        <div className="service-item">
          <img src={BrightLogo} className="service-icon" alt="BrightChamps Logo" />
          <h3>BrightChamps</h3>
          <p>Coding Instructor</p>
          <p id='des'>
            I worked as a Coding Instructor at BrightChamps Pvt. Ltd., where I taught diverse groups of students 
            and created tailored coding curriculums across various technologies such as HTML, JavaScript, Python, 
            and Machine Learning. (March 2021 – April 2022)
          </p>
          {isBrightChampsExpanded && (
            <div className="detailed-description">
              <p>
                At BrightChamps, I empowered students to build real-world applications using modern programming technologies. 
                I designed customized curriculums that catered to diverse student needs and backgrounds.
              </p>
              <p>
                My efforts resulted in a 25% increase in student retention and engagement through hands-on learning experiences.
              </p>
            </div>
          )}
          <a href="#learn-more" onClick={toggleBrightChampsDetails}>
            {isBrightChampsExpanded ? 'Show less' : 'Learn more'}
          </a>
        </div>
      </div>
    </section>
  );
};

export default Services;
